import React, { useState } from 'react';
import { FormGroup, Label, Input, StyledTextarea, SignupContainer, FormButton, ContactH1} from "./SignupElements";

const [state, setState] = useState(initalState);
  const [error, setError] = useState('');

  const initalState = {
    name: '',
    email: '',
    message: '',
    gender: ''
  };

const handleSubmit = e => {
    e.preventDefault();
    console.log('submitted!');
    console.log(state);

    for (let key in state) {
      if (state[key] === '') {
        setError(`You must provide the ${key}`)
        return
      }
    }
    setError('');
    // const regex = /^\w+([-+.']\w+)*@\w+([-.]\w+)*\.\w+([-.]\w+)*$/;
    // const test = regex.test(state.email);
    // console.log(test);

    console.log("Succeeded!!!")
  };

const Signup = () => {
  return (
    <SignupContainer id='contact'>
    <ContactH1>Contact Us</ContactH1>
    <FormGroup>
      <Label htmlFor="label">Name</Label>
      <Input id="label" />
      <Label>Email Address</Label>
      <Input />
      <Label>Phone</Label>
      <Input />
      <Label>State</Label>
      <Input />
      <Label>City</Label>
      <Input />
      <Label>Equipment</Label>
      <select id="job" name="field4">
        <optgroup label="Trucks">
        <option value="18wheeler">18 wheeler</option>
        <option value="flatBed">Flat Bed</option>
        <option value="boxTruck">Box Truck</option>
        <option value="other_indoor">Other</option>
        </optgroup>
        <optgroup label="Vans">
        <option value="53DryVan">53' Dry Van</option>
        <option value="53Reefer">53' Reefer</option>
        </optgroup>
        </select>      
    </FormGroup>
    <FormGroup>
    <Label>Subject</Label>
      <Input />
    <Label>Message:</Label>
    <StyledTextarea placeholder="Please enter your inquiry here"></StyledTextarea>
    </FormGroup>
    <FormButton>Send Message</FormButton>
    </SignupContainer>

  );
};

export default Signup;
